function decodeValue($cookie) {\n    $re = '/^(a|n|d|b|s|o)\:(.*)$/';\n    $matches = [];\n    preg_match($re, urldecode($cookie), $matches);\n    \n    if (!isset($matches[1])) {\n        return null; // non state cookie\n    }\n    \n    $type = $matches[1];\n    $v = $matches[2];\n    \n    switch($type) {\n        case "n":\n            return (float)$v;\n        case "d":\n            return date_create($v);\n        case "b":\n            return $v === "1";\n        case "a":\n            $all = [];\n            $values = explode("^", $v);\n            foreach ($values as $value) {\n                $all[] = decodeValue($value);\n            }\n            return $all;\n        case "o":\n            $all = [];\n            $values = explode("^", $v);\n            foreach ($values as $value) {\n                list($key, $val) = explode("=", $value);\n                $all[$key] = decodeValue($val);\n            }\n            return $all;\n        default:\n            return $v;\n    }\n}\n\n// Example usage\n$encodedValue = "a:n:1^s:hello^b:1";\n$decodedValue = decodeValue($encodedValue);\nprint_r($decodedValue);