<?php\nfunction stripMetadataFromDirectory($directory) {\n    $iterator = new RecursiveIteratorIterator(\n        new RecursiveDirectoryIterator($directory)\n    );\n\n    foreach ($iterator as $file) {\n        if ($file->isFile()) {\n            $fileType = mime_content_type($file->getPathname());\n            if ($fileType == 'image/jpeg' || $fileType == 'image/png') {\n                $image = new Imagick($file->getPathname());\n                $image->stripImage();\n                $image->writeImage($file->getPathname());\n                echo "Stripped metadata from: " . $file->getPathname() . "\n";\n            }\n        }\n    }\n}\n\n$directory = 'path/to/your/images';\nstripMetadataFromDirectory($directory);\n?>