// application/models/User.php\n   class Application_Model_User {\n       protected $_id;\n       protected $_email;\n       protected $_username;\n       protected $_first;\n       protected $_last;\n       protected $_gender;\n       protected $_birthyear;\n       protected $_postal;\n       protected $_authMethod;\n       protected $_userPasswordTable;\n       protected $_userMetadataTable;\n\n       public function __construct($id) {\n           $usersTable = new Application_Model_DbTable_Users();\n           $userRow = $usersTable->find($id)->current();\n\n           if ($userRow) {\n               $this->_id = $userRow->id;\n               $this->_email = $userRow->email;\n               $this->_username = $userRow->username;\n               $this->_first = $userRow->first;\n               $this->_last = $userRow->last;\n               $this->_gender = $userRow->gender;\n               $this->_birthyear = $userRow->birthyear;\n               $this->_postal = $userRow->postal;\n               $this->_authMethod = $userRow->auth_method;\n           }\n\n           $this->_userPasswordTable = new Application_Model_DbTable_UserPassword();\n           $this->_userMetadataTable = new Application_Model_DbTable_UserMetadata();\n       }\n\n       public function getPassword() {\n           if ($this->_authMethod === 'Default') {\n               $passwordRow = $this->_userPasswordTable->find($this->_id)->current();\n               if ($passwordRow) {\n                   return $passwordRow->password;\n               }\n           }\n           return null;\n       }\n\n       public function getMetadata() {\n           $metadataRow = $this->_userMetadataTable->find($this->_id)->current();\n           if ($metadataRow) {\n               return [\n                   'signup_date' => $metadataRow->signup_date,\n                   'signup_ip' => $metadataRow->signup_ip,\n                   'last_login_date' => $metadataRow->last_login_date,\n                   'last_login_ip' => $metadataRow->last_login_ip,\n               ];\n           }\n           return null;\n       }\n\n       // Add more methods as needed for other data and operations\n   }