$homePointsPlayerResult = mysql_query("\n    SELECT `player_id`, COUNT(*) as `conversions_count`\n    FROM `conversions`\n    WHERE `game_id` = '$game_id' AND `team_id` IS NULL\n    GROUP BY `player_id`\n");\n\nwhile ($row = mysql_fetch_assoc($homePointsPlayerResult)) {\n    if (array_search($row['player_id'], $home_players, true) === false) {\n        $home_players[] = array(\n            'player_id' => $row['player_id'],\n            'conversions' => $row['conversions_count']\n        );\n    }\n}