<?php\n$latitude = 51.5074;\n$longitude = -0.1278;\n$radius = 5000; // radius in meters\n\n$pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n$stmt = $pdo->prepare("\n    SELECT id, username, ST_Distance_Sphere(location, POINT(?, ?)) AS distance\n    FROM users\n    WHERE ST_Distance_Sphere(location, POINT(?, ?)) <= ?\n    ORDER BY distance\n");\n$stmt->execute([$latitude, $longitude, $latitude, $longitude, $radius]);\n$results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n\nforeach ($results as $user) {\n    echo "User: {$user['username']}, Distance: {$user['distance']} meters\n";\n}\n?>