<?php\n   $serverName = "myserver"; // This should match the name in freetds.conf\n   $username = "your_username";\n   $password = "your_password";\n   $database = "your_database";\n\n   $connection = sybase_connect($serverName, $username, $password);\n   if (!$connection) {\n       die('Connection failed: ' . sybase_get_last_message());\n   }\n\n   sybase_select_db($database, $connection);\n   \n   $query = "SELECT * FROM your_table";\n   $result = sybase_query($query, $connection);\n\n   while ($row = sybase_fetch_assoc($result)) {\n       print_r($row);\n   }\n\n   sybase_close($connection);\n   ?>