<?php\n$xmlContent = '\n<gesmes:Envelope xmlns:gesmes="http://www.gesmes.org/xml/2002-08-01">\n    <gesmes:subject>Reference rates</gesmes:subject>\n    <gesmes:Sender>\n        <gesmes:name>European Central Bank</gesmes:name>\n    </gesmes:Sender>\n    <Cube>\n        <Cube time="2009-07-13">\n            <Cube currency="USD" rate="1.3975"/>\n            <Cube currency="JPY" rate="129.03"/>\n            <Cube currency="BGN" rate="1.9558"/>\n            <Cube currency="CZK" rate="26.028"/>\n        </Cube>\n    </Cube>\n</gesmes:Envelope>';\n\n$dom = new DOMDocument();\n$dom->loadXML($xmlContent);\n$xpath = new DOMXPath($dom);\n$xpath->registerNamespace('gesmes', 'http://www.gesmes.org/xml/2002-08-01');\n\n// Example: Select all <Cube> elements with a 'currency' attribute\n$query = '//Cube[@currency]';\n$entries = $xpath->query($query);\n\nforeach ($entries as $entry) {\n    echo $entry->getAttribute('currency') . ': ' . $entry->getAttribute('rate') . "\n";\n}\n?>