<?php\n// Sample output with placeholders\n$output = "<p>Blah blah blah [[studentCount]]</p><p>Another field [[teacherCount]]</p>";\n\n// Function to replace placeholders with DB values\nfunction replacePlaceholders($output, $school) {\n    return preg_replace_callback('/\[\[(.*?)\]\]/', function ($matches) use ($school) {\n        $field = $matches[1];\n        return $school->get($field);\n    }, $output);\n}\n\n// Assuming $school is your database object\n// Replace placeholders in the output\n$updatedOutput = replacePlaceholders($output, $school);\n\n// Display the updated output\necho $updatedOutput;\n?>