// src/Core/Router.php\nnamespace App\Core;\n\nclass Router {\n    private $routes = [];\n\n    public function add($route, $callback) {\n        $this->routes[$route] = $callback;\n    }\n\n    public function dispatch($url) {\n        if (isset($this->routes[$url])) {\n            call_user_func($this->routes[$url]);\n        } else {\n            echo "404 Not Found";\n        }\n    }\n}