<?php\nfunction simple_hash($sender_id, $recipient_id, $secret_key) {\n    $data = $sender_id . ':' . $recipient_id;\n    $encrypted_data = openssl_encrypt($data, 'aes-128-ecb', $secret_key, OPENSSL_RAW_DATA);\n    return rtrim(strtr(base64_encode($encrypted_data), '+/', '-_'), '=');\n}\n\nfunction decode_hash($hash, $secret_key) {\n    $encrypted_data = base64_decode(strtr($hash, '-_', '+/'));\n    $decrypted_data = openssl_decrypt($encrypted_data, 'aes-128-ecb', $secret_key, OPENSSL_RAW_DATA);\n    list($sender_id, $recipient_id) = explode(':', $decrypted_data);\n    return array('sender_id' => $sender_id, 'recipient_id' => $recipient_id);\n}\n\n$secret_key = 'your-secret-key'; // Use a consistent secret key for encoding/decoding\n$sender_id = 123;\n$recipient_id = 456;\n\n$hash = simple_hash($sender_id, $recipient_id, $secret_key);\necho "Hash: " . $hash . "\n";\n\n$decoded_data = decode_hash($hash, $secret_key);\nprint_r($decoded_data);\n?>