// In app/Model/Address.php\n\nclass Address extends AppModel {\n    public function generateMapUrl($id = null) {\n        if (!$id) {\n            return 'Invalid Property Id';\n        }\n        \n        $address = $this->findById($id);\n        if (!$address) {\n            return 'Invalid Property Id';\n        }\n\n        $url = "http://maps.google.com/maps?oi=map&q=";\n        $url .= urlencode($address['Address']['street_num'] . ' ');\n        $url .= urlencode($address['Address']['street'] . ' ');\n        $url .= urlencode($address['Address']['city'] . ' ');\n        $url .= urlencode($address['Address']['province'] . ' ');\n        $url .= urlencode($address['Address']['country'] . ' ');\n        $url .= urlencode($address['Address']['postal_code']);\n\n        return $url;\n    }\n}