class Application_Form_Survey extends Zend_Form\n{\n    protected $questionsData;\n\n    public function __construct($questionsData = null)\n    {\n        $this->questionsData = $questionsData;\n        parent::__construct();\n    }\n\n    public function init()\n    {\n        if ($this->questionsData) {\n            foreach ($this->questionsData as $question) {\n                $element = new Zend_Form_Element_Radio($question['name']);\n                $element->setLabel($question['label']);\n\n                foreach ($question['options'] as $optionValue => $optionLabel) {\n                    $element->addMultiOption($optionValue, $optionLabel);\n                }\n\n                // Add validators, filters, etc., if needed\n                if (!empty($question['validators'])) {\n                    foreach ($question['validators'] as $validator) {\n                        $element->addValidator($validator);\n                    }\n                }\n\n                if (!empty($question['filters'])) {\n                    foreach ($question['filters'] as $filter) {\n                        $element->addFilter($filter);\n                    }\n                }\n\n                $this->addElement($element);\n            }\n        }\n    }\n}