class YourForm extends sfForm\n{\n    protected $user;\n\n    public function __construct($user, $defaults = array(), $options = array(), $CSRFSecret = null)\n    {\n        $this->user = $user;\n        parent::__construct($defaults, $options, $CSRFSecret);\n    }\n\n    public function configure()\n    {\n        $graduateJobTitle = $this->user->getAttribute('graduate_job_title', '');\n\n        $this->setWidgets(array(\n            'graduate_job_title' => new sfWidgetFormInput(array('default' => $graduateJobTitle), array('maxlength' => 80, 'size' => 30)),\n        ));\n\n        // Other form configuration...\n    }\n}