function latLongToXY($lat, $long, $imageWidth, $imageHeight) {\n    // Constants for the Equirectangular projection\n    $latOrigin = 0;\n    $longOrigin = 0;\n\n    // Convert degrees to radians\n    $latRad = deg2rad($lat);\n    $longRad = deg2rad($long);\n\n    // Calculate x and y using the Equirectangular projection\n    $x = ($long + 180) * ($imageWidth / 360); // map longitude to x\n    $y = ($imageHeight / 2) - ($imageWidth * (log(tan(M_PI / 4 + $latRad / 2)) / (2 * M_PI)));\n\n    return array('x' => round($x), 'y' => round($y));\n}\n\n// Example usage\n$lat = 40.7560540;\n$long = -73.9869510;\n$imageWidth = 1000;\n$imageHeight = 446;\n\n$coordinates = latLongToXY($lat, $long, $imageWidth, $imageHeight);\necho "X: " . $coordinates['x'] . ", Y: " . $coordinates['y'];