class ConditionGroup {\n    private $conditions = [];\n    private $operators = [];\n\n    public function addCondition(Condition $condition, $join = 'AND') {\n        $this->conditions[] = $condition;\n        $this->operators[] = $join;\n    }\n\n    public function validate() {\n        if (empty($this->conditions)) {\n            return true; // No conditions to validate, return true by default\n        }\n\n        $result = $this->conditions[0]->validate();\n        \n        for ($i = 1; $i < count($this->conditions); $i++) {\n            $operator = $this->operators[$i];\n            $conditionResult = $this->conditions[$i]->validate();\n\n            switch ($operator) {\n                case 'AND':\n                    $result = $result && $conditionResult;\n                    break;\n                case 'OR':\n                    $result = $result || $conditionResult;\n                    break;\n                case 'NOT':\n                    $result = $result && !$conditionResult;\n                    break;\n                default:\n                    throw new InvalidArgumentException("Unsupported operator '$operator'");\n            }\n        }\n\n        return $result;\n    }\n}\n\nclass Condition {\n    private $callback;\n\n    public function __construct(callable $callback) {\n        $this->callback = $callback;\n    }\n\n    public function validate() {\n        return call_user_func($this->callback);\n    }\n}\n\n// Example usage\n$conditionGroup = new ConditionGroup();\n\n$condition1 = new Condition(function() {\n    return true; // Apple is Red\n});\n$condition2 = new Condition(function() {\n    return true; // Apple is Ripe\n});\n$condition3 = new Condition(function() {\n    return true; // Apple is Golden Delicious\n});\n\n$conditionGroup->addCondition($condition1);\n$conditionGroup->addCondition($condition2, 'AND');\n$conditionGroup->addCondition($condition3, 'NOT');\n\n$result = $conditionGroup->validate();\nvar_dump($result); // bool(false) because the last condition is NOT true