<?php\nerror_reporting(E_ALL);\nini_set('display_errors', 1);\n\nrequire_once('lib/nusoap.php'); \n$wsdl = "http://localhost:8080/HelloWorldWS/sayHiService?WSDL";\n$client = new nusoap_client($wsdl, 'wsdl');\n\n// Check for construction errors\n$err = $client->getError();\nif ($err) {\n    echo 'Constructor error: ' . $err;\n    exit();\n}\n\n// Set parameter as an associative array\n$param = array('name' => 'John');\n\n// Call the method and get the response\n$response = $client->call('Hey', $param);\n\n// Check for a fault\nif ($client->fault) {\n    echo 'Fault: ';\n    print_r($response);\n} else {\n    // Check for errors\n    $err = $client->getError();\n    if ($err) {\n        echo 'Error: ' . $err;\n    } else {\n        // Display the result\n        echo 'Response: ';\n        print_r($response);\n    }\n}\n\nunset($client);\n?>