<?php\n   // Get the host\n   $host = $_SERVER['HTTP_HOST'];\n\n   // Extract the subdomain\n   $parts = explode('.', $host);\n   $subdomain = $parts[0];\n\n   // Check if it's a valid user\n   if ($subdomain != 'www' && $subdomain != 'website' && !empty($subdomain)) {\n       // Handle the subdomain logic\n       $userDirectory = "/path/to/user/content/" . $subdomain;\n       if (file_exists($userDirectory)) {\n           // Serve user-specific content\n           include($userDirectory . '/index.php');\n       } else {\n           // Handle non-existent user\n           header("HTTP/1.0 404 Not Found");\n           echo "User not found.";\n       }\n   } else {\n       // Serve the main website\n       include('/path/to/main/website/index.php');\n   }\n   ?>