function flattenArray($array) {\n    $result = array();\n\n    foreach ($array as $element) {\n        if (is_array($element)) {\n            $result = array_merge($result, flattenArray($element));\n        } else {\n            $result[] = $element;\n        }\n    }\n\n    return $result;\n}\n\n// Example usage\n$multiDimArray = array(\n    1,\n    array(2, 3, array(4, 5)),\n    6,\n    array(7, array(8, 9))\n);\n\n$flatArray = flattenArray($multiDimArray);\nprint_r($flatArray);