// Pseudocode for email verification\nif (isset($_POST['vote'])) {\n    $email = $_POST['email'];\n    if (filter_var($email, FILTER_VALIDATE_EMAIL)) {\n        // Check if email has already been used to vote\n        $result = $db->query("SELECT * FROM votes WHERE email = '$email'");\n        if ($result->num_rows === 0) {\n            // Send verification email\n            send_verification_email($email);\n            echo "A verification email has been sent to your address.";\n        } else {\n            echo "This email has already been used to vote.";\n        }\n    } else {\n        echo "Invalid email address.";\n    }\n}