<?php\n// Initialize a cURL session\n$ch = curl_init();\n\n// Set the URL of the page you want to retrieve\ncurl_setopt($ch, CURLOPT_URL, 'http://example.com/page');\n\n// Set it to return the transfer as a string of the return value of curl_exec()\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\n\n// Execute the session and store the result in a variable\n$html = curl_exec($ch);\n\n// Close the cURL session\ncurl_close($ch);\n\n// Create a new DOMDocument instance\n$dom = new DOMDocument();\n\n// Suppress errors due to malformed HTML\nlibxml_use_internal_errors(true);\n\n// Load the HTML content into the DOMDocument instance\n$dom->loadHTML($html);\n\n// Clear the libxml error buffer\nlibxml_clear_errors();\n\n// Create a new DOMXPath instance\n$xpath = new DOMXPath($dom);\n\n// Query the DOM for the image inside the div with id 'image'\n$imageNode = $xpath->query('//div[@id="image"]//img')->item(0);\n\nif ($imageNode) {\n    // Get the 'src' attribute of the image node\n    $src = $imageNode->getAttribute('src');\n    echo "Image src: " . $src;\n} else {\n    echo "Image not found";\n}\n?>