public function confirmImage($imageId)\n{\n    $image = $this->Images->get($imageId);\n    $finalPath = Configure::read('Upload.finalPath') . $image->filename;\n\n    // Move file to final path\n    if (rename($image->path, $finalPath)) {\n        $image->path = $finalPath;\n        $image->status = 'confirmed'; // Update status\n        $this->Images->save($image);\n\n        $this->Flash->success(__('Image confirmed and saved.'));\n    } else {\n        $this->Flash->error(__('Failed to confirm image.'));\n    }\n}