<?php\ntry {\n    $rar_file = rar_open('htdocs.rar');\n    if ($rar_file === FALSE) {\n        die("Can't open Rar archive");\n    }\n\n    $entries = rar_list($rar_file);\n\n    foreach ($entries as $entry) {\n        echo 'Filename: ' . $entry->getName() . "\n";\n        echo 'Packed size: ' . $entry->getPackedSize() . "\n";\n        echo 'Unpacked size: ' . $entry->getUnpackedSize() . "\n";\n\n        $entry->extract('C:/xampp/htdocs');\n    }\n\n    rar_close($rar_file);\n} catch (Exception $e) {\n    echo 'Caught exception: ',  $e->getMessage(), "\n";\n}\n?>