// Example array returned from Zend_DB query\n$array = array(\n    1 => array(\n        "comment_id" => "1",\n        "erasable" => "1",\n        "comment" => "test 1"\n    ),\n    2 => array(\n        "comment_id" => "2",\n        "erasable" => "1",\n        "comment" => "test 1"\n    ),\n    3 => array(\n        "comment_id" => "3",\n        "erasable" => "1",\n        "comment" => "jhghjg"\n    )\n);\n\n// Reindex the array to ensure it has sequential numeric keys starting from 0\n$array = array_values($array);\n\n// Encode the array as JSON\n$json = json_encode($array);\n\n// Output the JSON\necho $json;