<?php\n\nfunction generateUniqueCodes($count, $length) {\n    $charset = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';\n    $charsetLength = strlen($charset);\n    $codes = [];\n\n    while (count($codes) < $count) {\n        $code = '';\n        for ($i = 0; $i < $length; $i++) {\n            $code .= $charset[random_int(0, $charsetLength - 1)];\n        }\n\n        // Ensure uniqueness\n        if (!isset($codes[$code])) {\n            $codes[$code] = true;\n        }\n    }\n\n    return array_keys($codes);\n}\n\n// Parameters\n$totalCodes = 63000000;\n$codeLength = 10;\n\n// Generate codes\n$uniqueCodes = generateUniqueCodes($totalCodes, $codeLength);\n\n// Output example\nforeach (array_slice($uniqueCodes, 0, 10) as $code) {\n    echo $code . PHP_EOL;\n}\n\n?>