<?php\nsession_start(); // Start the session\n\nclass DatabaseConnection {\n    private $connection;\n\n    public function __construct($host, $username, $password, $dbname) {\n        $this->connection = new mysqli($host, $username, $password, $dbname);\n\n        if ($this->connection->connect_error) {\n            die("Connection failed: " . $this->connection->connect_error);\n        }\n    }\n\n    public function getConnection() {\n        return $this->connection;\n    }\n\n    public function __destruct() {\n        if ($this->connection) {\n            $this->connection->close();\n        }\n    }\n}\n\n// Check if the database connection is already in the session\nif (!isset($_SESSION['db_connection'])) {\n    // Create a new database connection and store it in the session\n    $_SESSION['db_connection'] = new DatabaseConnection('localhost', 'username', 'password', 'database');\n}\n\n// Use the database connection from the session\n$db_connection = $_SESSION['db_connection']->getConnection();\n\n// Example query\n$result = $db_connection->query("SELECT * FROM some_table");\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "id: " . $row["id"]. " - Name: " . $row["name"]. "<br>";\n    }\n} else {\n    echo "0 results";\n}\n?>