class MyWrongTypeException extends RuntimeException {\n    public function __construct($objectName, $object, $expectedType, $code = 0, Exception $previous = null) {\n        $receivedType = gettype($object);\n        $message = "Wrong Type: '$objectName'. Expected '$expectedType', received '$receivedType'.";\n        if (App::get_mode() == 'debug') {\n            $message .= " Debug Info: " . var_export($object, true);\n        }\n        parent::__construct($message, $code, $previous);\n    }\n}\n\n// Example usage\n$users = get_users(); // Hypothetical function that should return an array\nif (!is_array($users)) {\n    throw new MyWrongTypeException('$users', $users, 'array');\n}