<?php\n// Assuming you have already established a PDO connection named $pdo\n// Example: $pdo = new PDO('mysql:host=hostname;dbname=database', 'username', 'password');\n\necho '<td background="images/map/';\n\n$horizontal = ${'mapPiece' . $mapPieceCount}[0];\n$vertical = ${'mapPiece' . $mapPieceCount}[0];\n\n$sql = "SELECT image FROM map WHERE horizontal = :horizontal AND vertical = :vertical";\n$stmt = $pdo->prepare($sql);\n$stmt->execute(['horizontal' => $horizontal, 'vertical' => $vertical]);\n\n$tile = $stmt->fetch(PDO::FETCH_ASSOC);\n\nif ($tile) {\n    echo htmlspecialchars($tile['image'], ENT_QUOTES, 'UTF-8');\n}\n\necho '.png"></td>';\n?>