require 'twitter.lib.php'; // Include the twitterlibphp library\n\n$twitter = new Twitter('username', 'password'); // Authenticate with Twitter\n\n// Get the friends timeline with a limit of 1 status\n$statuses = $twitter->getFriendsTimeline(array('count' => 1));\n\n// Loop through each status and make the links clickable\nforeach ($statuses as $status) {\n    $status->text = clickable_link($status->text);\n    echo "<p>{$status->text}</p>";\n}