class IndexController extends Zend_Controller_Action\n    {\n        public function preDispatch()\n        {\n            $acl = new MyAcl();\n            $role = Zend_Auth::getInstance()->getIdentity()->role; // Assuming you have a role in the identity\n            $resource = $this->_request->getControllerName();\n            $action = $this->_request->getActionName();\n\n            $resourceName = $resource . ':' . $action;\n\n            if (!$acl->isAllowed($role, $resourceName)) {\n                // Redirect or show an error\n                $this->_redirect('error/access-denied');\n            }\n        }\n\n        public function indexAction()\n        {\n            // Regular action logic\n        }\n    }