<?php\n// Initialize the arrays\n$array1 = array("txt1", "txt2", "txt3");\n$array2 = array(0 => "on", 2 => "on");\n\n// Initialize the final array\n$finalArray = array();\n\n// Iterate through $array2 and fetch corresponding values from $array1\nforeach ($array2 as $key => $value) {\n    if (isset($array1[$key])) {\n        $finalArray[$key] = $array1[$key];\n    }\n}\n\n// Reset the keys to be sequential\n$finalArray = array_values($finalArray);\n\n// Output the final array\nprint_r($finalArray);\n?>