<?php\n$how_many = 1000000;\n$counter1 = 0;\n$counter2 = 0;\n\n$abc = array('boolean_lookup' => TRUE, 'string_lookup' => 'something_else');\n\n// Boolean lookup test\n$start = microtime(true);\nfor ($i = 0; $i < $how_many; $i++) {\n    if ($abc['boolean_lookup']) {\n        $counter1++;\n    }\n}\n$end = microtime(true);\necho 'Boolean lookup time: ' . ($end - $start) . ' seconds<br>';\n\n// String lookup test\n$start = microtime(true);\nfor ($i = 0; $i < $how_many; $i++) {\n    if ($abc['string_lookup'] == 'something_else') {\n        $counter2++;\n    }\n}\n$end = microtime(true);\necho 'String lookup time: ' . ($end - $start) . ' seconds<br>';\n?>