<?php\n$servername = "your_server_name";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database_name";\n\n// Create connection\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get current date and date 1 day ago\n$current_date = date('Y-m-d');\n$yesterday_date = date('Y-m-d', strtotime('-1 day'));\n\n// Fetch records that are 1 day old\n$sql = "SELECT Name, Address, Data, email, Date FROM your_table_name WHERE Date = '$yesterday_date'";\n$result = $conn->query($sql);\n\n// Check if there are records to process\nif ($result->num_rows > 0) {\n    // Loop through each record\n    while($row = $result->fetch_assoc()) {\n        $name = $row["Name"];\n        $email = $row["email"];\n        $data = $row["Data"];\n        \n        // Prepare email\n        $subject = "Data Reminder";\n        $message = "Hello $name,\n\nThis is a reminder that your data is: $data.";\n        $headers = "From: your_email@example.com";\n\n        // Send email\n        if (filter_var($email, FILTER_VALIDATE_EMAIL)) {\n            mail($email, $subject, $message, $headers);\n        }\n    }\n} else {\n    echo "No records found.";\n}\n\n$conn->close();\n?>