<?php\nrequire 'PHPMailer/PHPMailerAutoload.php';\n\n// FTP server details\n$ftp_server = 'ftp.example.com';\n$ftp_username = 'your_ftp_username';\n$ftp_password = 'your_ftp_password';\n$remote_file_path = '/path/to/remote/file.pdf';\n$local_file_path = '/path/to/local/file.pdf';\n\n// Establish FTP connection\n$ftp_conn = ftp_connect($ftp_server) or die("Could not connect to $ftp_server");\n\n// Login to FTP server\nif (@ftp_login($ftp_conn, $ftp_username, $ftp_password)) {\n    // Download the file from FTP server\n    if (ftp_get($ftp_conn, $local_file_path, $remote_file_path, FTP_BINARY)) {\n        echo "File downloaded successfully.";\n    } else {\n        echo "Error downloading file.";\n    }\n} else {\n    echo "Could not log in to $ftp_server.";\n}\n\n// Close the FTP connection\nftp_close($ftp_conn);\n\n// Create a new PHPMailer instance\n$mail = new PHPMailer;\n\n// Set mailer to use SMTP\n$mail->isSMTP();\n$mail->Host = 'smtp.example.com';  // Specify main and backup SMTP servers\n$mail->SMTPAuth = true;            // Enable SMTP authentication\n$mail->Username = 'your_email@example.com'; // SMTP username\n$mail->Password = 'your_email_password';    // SMTP password\n$mail->SMTPSecure = 'tls';         // Enable TLS encryption, `ssl` also accepted\n$mail->Port = 587;                 // TCP port to connect to\n\n// Set email parameters\n$mail->setFrom('from@example.com', 'Mailer');\n$mail->addAddress('client@example.com', 'Client Name'); // Add a recipient\n$mail->Subject = 'Here is your artwork';\n$mail->Body    = 'Please find the attached artwork.';\n\n// Attach the downloaded file\n$mail->addAttachment($local_file_path);\n\n// Send the email\nif(!$mail->send()) {\n    echo 'Message could not be sent.';\n    echo 'Mailer Error: ' . $mail->ErrorInfo;\n} else {\n    echo 'Message has been sent';\n}\n\n// Delete the local file if needed\nunlink($local_file_path);\n?>