<?php\n    use Zend\Permissions\Acl\Acl;\n    use Zend\Permissions\Acl\Role\GenericRole as Role;\n    use Zend\Permissions\Acl\Resource\GenericResource as Resource;\n\n    class MyAcl extends Acl\n    {\n        public function __construct()\n        {\n            // Define roles\n            $this->addRole(new Role('normal'));\n            $this->addRole(new Role('admin'));\n\n            // Define resources\n            $this->addResource(new Resource('default:index'));\n            $this->addResource(new Resource('default:admin'));\n\n            // Assign permissions\n            $this->allow('normal', 'default:index');\n            $this->allow('admin', 'default:admin');\n            $this->allow('admin', 'default:index');\n        }\n    }