function test($var)\n{\n    if (!isset($_SESSION['myarray']) || !is_array($_SESSION['myarray'])) {\n        $_SESSION['myarray'] = [];\n    }\n\n    array_push($_SESSION['myarray'], $var);\n\n    if (count($_SESSION['myarray']) > 10) {\n        array_shift($_SESSION['myarray']); // Remove the first element to maintain the length of 10\n    }\n}\n\nsession_start(); // Make sure to start the session\ntest("hello");