<?php\n// Database connection\n$servername = "localhost";\n$username = "****";\n$password = "****";\n$dbname = "*****";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Get value coming from request\n$search = $conn->real_escape_string($_GET['search']);\n\n$sql = "SELECT title FROM dbArticle WHERE title LIKE '%$search%' OR type LIKE '%$search%' OR username LIKE '%$search%'";\n$result = $conn->query($sql);\n\nif ($result->num_rows > 0) {\n    while($row = $result->fetch_assoc()) {\n        echo "<div class='suggestion' onclick='selectTitle(\"" . addslashes($row['title']) . "\")'>" . $row['title'] . "</div>";\n    }\n} else {\n    echo "No results found";\n}\n\n$conn->close();\n?>