$url = "http://apple.accuweather.com/adcbin/apple/Apple_Weather_Data.asp?zipcode=EUR;PT;PO019;REGUA";\n\n// Initialize cURL session\n$ch = curl_init();\n\n// Set cURL options\ncurl_setopt($ch, CURLOPT_URL, $url);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\ncurl_setopt($ch, CURLOPT_CONNECTTIMEOUT, 10); // Timeout for connection\ncurl_setopt($ch, CURLOPT_TIMEOUT, 20); // Timeout for response\n\n// Execute cURL session and fetch data\n$response = curl_exec($ch);\n\n// Check for cURL errors\nif (curl_errno($ch)) {\n    echo 'Curl error: ' . curl_error($ch);\n} else {\n    // Load XML from response\n    $xmlobj = simplexml_load_string($response);\n    if ($xmlobj === false) {\n        echo "Failed to load XML";\n    } else {\n        header("Content-type: text/plain");\n        print_r($xmlobj);\n    }\n}\n\n// Close cURL session\ncurl_close($ch);