<?php\n\nif (isset($_GET['user_id'])) {\n    $content = 'user_id';\n    $user_id = $_GET['user_id']; // Capture user_id for later use\n} else {\n    $content = isset($_GET['content']) ? $_GET['content'] : 'default';\n}\n\nswitch($content) {\n\n// The default page\ndefault:\n    include_once('main.php');\n    break;\n\n// The news related stuff\ncase 'news':\n    include_once('news.php');\n    break;\n\n// Show the profile of a user\ncase 'user_id':\n    include_once('profile.php');\n    // You can use $user_id here if needed\n    break;\n}\n?>