public function cleanUpTemporaryFiles()\n{\n    $this->loadModel('Images');\n    $expirationTime = new \DateTime('-1 day'); // Example: 1 day old files\n\n    $temporaryImages = $this->Images->find()\n        ->where(['status' => 'temporary', 'created <' => $expirationTime]);\n\n    foreach ($temporaryImages as $image) {\n        if (file_exists($image->path)) {\n            unlink($image->path); // Delete file\n        }\n        $this->Images->delete($image); // Remove database entry\n    }\n}