<?php\n   $stmt = $pdo->query("\n       SELECT site_id, hit_type, COUNT(*) as hit_count\n       FROM hits\n       WHERE hit_timestamp >= NOW() - INTERVAL 24 HOUR\n       GROUP BY site_id, hit_type\n       ORDER BY hit_count DESC\n   ");\n\n   $rankings = $stmt->fetchAll(PDO::FETCH_ASSOC);\n   \n   foreach ($rankings as $ranking) {\n       echo "Site ID: " . htmlspecialchars($ranking['site_id']) . \n            " - Hit Type: " . htmlspecialchars($ranking['hit_type']) . \n            " - Count: " . htmlspecialchars($ranking['hit_count']) . "<br>";\n   }\n   ?>