<?php\n    error_reporting(E_ALL);\n\n    $src = escapeshellarg("/var/videos/video1.wmv");\n    $command = "/usr/bin/ffmpeg -i $src 2>&1";\n    \n    echo "<b>$command</b><br/>";\n\n    echo "shell_exec:<br/><pre>";\n    echo shell_exec($command);\n    echo "</pre><br/>";\n\n    echo "exec:<br/><pre>";\n    $output = array();\n    exec($command, $output, $status);\n    foreach ($output as $o) {\n        echo htmlspecialchars($o, ENT_QUOTES) . "<br/>";\n    }\n    echo "</pre><br/>";\n\n    echo "popen:<br/><pre>";\n    $handle = popen($command, 'r');\n    echo fread($handle, 1048576);\n    pclose($handle);\n    echo "</pre><br/>";\n?>