<?php\n// Array of image URLs\n$imageUrls = [\n    'http://domain.com/pic1.jpg',\n    'http://domain.com/pic2.jpg',\n    // Add more URLs as needed\n];\n\nforeach ($imageUrls as $url) {\n    // Get the content of the image\n    $imageContent = file_get_contents($url);\n\n    // Ensure the content was fetched successfully\n    if ($imageContent === FALSE) {\n        echo "Failed to fetch image from URL: $url\n";\n        continue;\n    }\n\n    // Parse the URL to get the image file name\n    $parsedUrl = parse_url($url);\n    $imageName = basename($parsedUrl['path']);\n\n    // Define the path where the image will be saved\n    $savePath = 'uploads/' . $imageName;\n\n    // Save the image to the server\n    if (file_put_contents($savePath, $imageContent) !== FALSE) {\n        echo "Image saved successfully: $savePath\n";\n    } else {\n        echo "Failed to save image: $savePath\n";\n    }\n}\n?>