<?php\nfunction isValidUrl($url) {\n    // Check if the URL is valid\n    if (filter_var($url, FILTER_VALIDATE_URL) === false) {\n        return false;\n    }\n\n    // Parse the URL to get the host\n    $host = parse_url($url, PHP_URL_HOST);\n\n    // Define your own domain (ensure this matches your actual domain)\n    $yourDomain = 'yourdomain.com';\n\n    // Check if the host contains your domain\n    if (strpos($host, $yourDomain) !== false) {\n        return false;\n    }\n\n    return true;\n}\n\n$url = 'http://www.google.com';\nif (isset($_POST['_check'])) {\n    $inputUrl = $_POST['url'];\n    if (isValidUrl($inputUrl)) {\n        $url = $inputUrl;\n    } else {\n        die("Invalid URL or URL from your domain is not allowed.");\n    }\n}\n\n// Gets the title from the selected page\n$file = @fopen($url, "r") or die("Can't read input stream");\n$text = fread($file, 16384);\nfclose($file);\n\nif (preg_match('/<title>(.*?)<\/title>/is', $text, $found)) {\n    $title = $found[1];\n} else {\n    $title = "Untitled Document";\n}\n?>