<?php\n// Define the timezones you want to get the time for\n$timezones = ['America/New_York', 'Europe/London', 'Asia/Tokyo', 'Australia/Sydney'];\n\n// Iterate through each timezone and get the current time\nforeach ($timezones as $timezone) {\n    $date = new DateTime('now', new DateTimeZone($timezone));\n    echo 'Current time in ' . $timezone . ': ' . $date->format('Y-m-d H:i:s') . "\n";\n}\n?>