function encryptCookie($data, $key) {\n    $iv = openssl_random_pseudo_bytes(openssl_cipher_iv_length('aes-256-cbc'));\n    $encrypted = openssl_encrypt($data, 'aes-256-cbc', $key, 0, $iv);\n    $hmac = hash_hmac('sha256', $encrypted, $key);\n    $encrypted = base64_encode($encrypted . '::' . $iv . '::' . $hmac);\n    return $encrypted;\n}\n\nfunction decryptCookie($data, $key) {\n    list($encrypted_data, $iv, $hmac) = explode('::', base64_decode($data), 3);\n    $calculated_hmac = hash_hmac('sha256', $encrypted_data, $key);\n    if (hash_equals($calculated_hmac, $hmac)) {\n        return openssl_decrypt($encrypted_data, 'aes-256-cbc', $key, 0, $iv);\n    }\n    return false; // HMAC validation failed\n}