<?php\n// Database connection\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to get ancestor-descendant relationships\n$query = "SELECT descendant AS id, GROUP_CONCAT(ancestor ORDER BY ancestor DESC) as ancestors \n          FROM closure \n          GROUP BY descendant";\n$result = $mysqli->query($query);\n\n// Fetch data\n$nodes = [];\nwhile ($row = $result->fetch_assoc()) {\n    $nodes[] = $row;\n}\n\n$mysqli->close();\n?>