function fnSanitizePost($data) {\n    // Define a list of unwanted characters and their replacements\n    $unwanted_chars = [\n        'â€™' => "'", 'â€œ' => '"', 'â€' => '"', 'â€˜' => "'", 'â€”' => '-', \n        'â€“' => '-', 'â€¦' => '...'\n    ];\n\n    if (is_array($data)) {\n        $areturn = array();\n        foreach ($data as $skey => $svalue) {\n            $areturn[$skey] = fnSanitizePost($svalue);\n        }\n        return $areturn;\n    } else {\n        if (!is_numeric($data)) {\n            // Remove magic quotes if enabled\n            if (get_magic_quotes_gpc()) {\n                $data = stripslashes($data);\n            }\n\n            // Replace unwanted characters\n            $data = strtr($data, $unwanted_chars);\n\n            // Escape string for database insertion\n            $data = pg_escape_string($data);\n\n            // Strip HTML and PHP tags\n            $data = strip_tags($data);\n        }\n\n        // Trim whitespace\n        $data = trim($data);\n        \n        return $data;\n    }\n}