<?php\n// Your malformed HTML snippet\n$html = <<<HTML\n<!-- Your HTML snippet here -->\nHTML;\n\n// Configure Tidy\n$config = array(\n    'clean' => true,\n    'output-xhtml' => true,\n    'show-body-only' => true,\n    'wrap' => 0\n);\n\n// Initialize Tidy\n$tidy = new tidy;\n$tidy->parseString($html, $config, 'utf8');\n$tidy->cleanRepair();\n\n// Load the cleaned HTML into DOMDocument\n$dom = new DOMDocument;\n@$dom->loadHTML($tidy->value);\n\n// Use DOMXPath to query the DOM\n$xpath = new DOMXPath($dom);\n\n// Example: Extract Home Team and Away Team names\n$homeTeam = $xpath->query("//text()[contains(., 'Home Team')]/following-sibling::text()")->item(0)->nodeValue;\n$awayTeam = $xpath->query("//text()[contains(., 'Away Team')]/following-sibling::text()")->item(0)->nodeValue;\n\necho "Home Team: " . trim($homeTeam) . "\n";\necho "Away Team: " . trim($awayTeam) . "\n";\n\n// Additional parsing logic goes here, for example extracting roster, goals, penalties, etc.\n// Example: Extract all player names in rosters\n$players = $xpath->query("//text()[contains(., 'Rosters')]/following::tr");\nforeach ($players as $player) {\n    echo $player->nodeValue . "\n";\n}\n?>