<?php\nob_start(); // Start output buffering\n\n// Your page content and logic here\n// ...\n\n$query = "SELECT * FROM products WHERE ..."; // Your dynamic query based on user selections\n$result = mysqli_query($connection, $query);\n\nif (mysqli_num_rows($result) == 0) {\n    // No results found, clear user choices and re-process the page\n    ob_clean(); // Clear the current output buffer\n    // Reset user selections here\n    // Example: $_SESSION['user_selections'] = [];\n    \n    // Optionally, you can redirect to the same page to start over\n    header("Location: " . $_SERVER['PHP_SELF']);\n    exit; // Make sure to exit after sending the header\n}\n\n$content = ob_get_clean(); // Get the buffered content and clean the buffer\necho $content; // Output the final content\n?>