function outputscores($mysqli_result, $reverse = false) {\n    $rows = [];\n    while ($row = mysqli_fetch_array($mysqli_result, MYSQLI_ASSOC)) {\n        $rows[] = $row;\n    }\n\n    if ($reverse) {\n        $rows = array_reverse($rows);\n    }\n\n    foreach ($rows as $row) {\n        echo '<img src="img/date.png" /> ' . date("j M Y", strtotime($row['timestamp'])) . '\n            <img src="img/time.png" /> ' . date("H:i:s", strtotime($row['timestamp'])) . '\n            <img src="img/star.png" /> ' . $row['score'] . $_GET["scoretype"] . '<br />';\n    }\n}\n\n// Example usage with mysqli:\n$mysqli = new mysqli("hostname", "username", "password", "database");\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n$result = $mysqli->query("SELECT * FROM $gid LIMIT 25");\nif (!$result) {\n    die("Query failed: " . $mysqli->error);\n}\n\noutputscores($result, true);\n$mysqli->close();